var site="/jonR_folio",app=angular.module("app",["ngRoute"]);app.config(function(t,e){t.when("/",{templateUrl:"templates/intro.html",controller:"Main"}).when("/folio",{templateUrl:"templates/work.html",controller:"Work"}).when("/experiments",{templateUrl:"templates/work.html",controller:"Play"}).when("/work/:slug",{templateUrl:"templates/demoframe.html"}).otherwise({redirectTo:"/"})}).controller("Main",function(t,e,o){}).controller("Content",function(t,e,o){}).controller("Work",["$scope","getPosts",function(t,e){e("portfolio").then(function(e){t.posts=e})}]).controller("Play",["$scope","getPosts",function(t,e){e("experiments").then(function(e){t.posts=e})}]).service("getPosts",function(t){return function(e){return t.get("lib/data/links.json").then(function(t){return t.data[e]},function(t){console.log(t)})}});
